$(".bt_1").click(function(){
   $("div").hide();
   });

$(".bt_2").click(function(){
    $("div").fadeIn(2000);
   });

$(".bt_3").click(function(){
    $("div").fadeOut();
  });

$(".bt_4").click(function(){
    $("div").show(1000);
   });
   
$(".bt_5").click(function(){
    $("div").slideUp(1000);
   });


     